/**
 * @title AirDrop
 * @dev Upgradeable Merkle airdrop contract with UUPS proxy pattern
 * @author nexm
 */
contract AirDrop is Initializable, AccessControlUpgradeable, UUPSUpgradeable, PausableUpgradeable {
    // Custom errors for gas efficiency
    error AlreadyClaimed();
    error InvalidProof();
    error MerkleRootNotSet();
    error InvalidAmount();
    error InvalidTokenAddress();
    error UpgradeAmountTooHigh();
    error ContractPaused();

    // Roles
    bytes32 public constant ROOT_SETTER_ROLE = keccak256("ROOT_SETTER_ROLE");
    bytes32 public constant UPGRADER_ROLE = keccak256("UPGRADER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    // State variables
    bytes32 public merkleRoot;
    MyMintableToken public token;
    mapping(address => bool) public claimed;
    uint256 public upgradeAmount;
    uint256 public totalClaimed;
    uint256 public totalClaimers;

    // Events
    event MerkleRootUpdated(bytes32 indexed newRoot);
    event Claimed(address indexed account, uint256 amount);
    event UpgradeAmountUpdated(uint256 indexed newAmount);
    event EmergencyWithdraw(address indexed token, uint256 amount);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Sets the upgrade amount (only admin)
     * @param _upgradeAmount The new upgrade amount
     */
    function setUpgradeAmount(uint256 _upgradeAmount) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (_upgradeAmount > 1000000 * 10**18) revert UpgradeAmountTooHigh();
        upgradeAmount = _upgradeAmount;
        emit UpgradeAmountUpdated(_upgradeAmount);
    }

    /**
     * @dev Initializes the contract
     * @param tokenAddress The address of the token contract
     * @param root The Merkle root hash
     */
    function initialize(address tokenAddress, bytes32 root) initializer public {
        __AccessControl_init();
        __UUPSUpgradeable_init();
        __Pausable_init();

        if (tokenAddress == address(0)) revert InvalidTokenAddress();
        if (root == bytes32(0)) revert MerkleRootNotSet();

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ROOT_SETTER_ROLE, msg.sender);
        _grantRole(UPGRADER_ROLE, msg.sender);
        _grantRole(PAUSER_ROLE, msg.sender);

        token = MyMintableToken(tokenAddress);
        merkleRoot = root;
    }

    /**
     * @dev Sets a new Merkle root (only root setter role)
     * @param root The new Merkle root hash
     */
    function setMerkleRoot(bytes32 root) external onlyRole(ROOT_SETTER_ROLE) {
        if (root == bytes32(0)) revert MerkleRootNotSet();
        merkleRoot = root;
        emit MerkleRootUpdated(root);
    }

    /**
     * @dev Claims tokens for a whitelisted address
     * @param amount The amount of tokens to claim
     * @param proof The Merkle proof for verification
     */
    function claim(uint256 amount, bytes32[] calldata proof) external whenNotPaused {
        if (claimed[msg.sender]) revert AlreadyClaimed();
        if (merkleRoot == bytes32(0)) revert MerkleRootNotSet();
        if (amount == 0) revert InvalidAmount();

        // Create leaf hash using abi.encode for security
        bytes32 leaf = keccak256(abi.encode(msg.sender, amount));
        if (!MerkleProof.verify(proof, merkleRoot, leaf)) revert InvalidProof();

        claimed[msg.sender] = true;
        totalClaimed += amount;
        totalClaimers += 1;

        // Mint token directly to the claimer
        token.mint(msg.sender, amount);
        emit Claimed(msg.sender, amount);
    }

    /**
     * @dev Pauses the contract (only pauser role)
     */
    function pause() external onlyRole(PAUSER_ROLE) {
        _pause();
    }

    /**
     * @dev Unpauses the contract (only pauser role)
     */
    function unpause() external onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    /**
     * @dev Emergency withdraw function for stuck tokens
     * @param tokenAddress The token address to withdraw
     * @param amount The amount to withdraw
     */
    function emergencyWithdraw(address tokenAddress, uint256 amount) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (tokenAddress == address(0)) revert InvalidTokenAddress();
        if (amount == 0) revert InvalidAmount();

        IERC20(tokenAddress).transfer(msg.sender, amount);
        emit EmergencyWithdraw(tokenAddress, amount);
    }

    /**
     * @dev Returns claim statistics
     * @return _totalClaimed Total amount claimed
     * @return _totalClaimers Total number of claimers
     */
    function getClaimStats() external view returns (uint256 _totalClaimed, uint256 _totalClaimers) {
        return (totalClaimed, totalClaimers);
    }

    /**
     * @dev Authorizes contract upgrades (only upgrader role)
     * @param newImplementation The address of the new implementation
     */
    function _authorizeUpgrade(address newImplementation)
        internal
        onlyRole(UPGRADER_ROLE)
        override
    {
        // Additional validation can be added here
        if (newImplementation == address(0)) revert InvalidTokenAddress();
    }

    // Reserve storage for future upgrades
    uint256[45] private __gap;
}

contracts/AirDropV2.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./AirDrop.sol";

/**
 * @title AirDropV2
 * @dev Upgraded version of AirDrop with additional features
 * @author nexm
 */
contract AirDropV2 is AirDrop {
    // New state variables (must be added after existing ones)
    string public version;
    bytes32[] public merkleRoots;
    mapping(bytes32 => bool) public usedRoots;

    // New events
    event BatchMerkleRootUpdated(bytes32[] newRoots);
    event VersionUpdated(string newVersion);

    /**
     * @dev Initializes V2 specific features
     */
    function initializeV2() external reinitializer(2) {
        version = "V2";
        emit VersionUpdated("V2");
    }

    /**
     * @dev Sets multiple Merkle roots for batch operations
     * @param roots Array of Merkle root hashes
     */
    function setBatchMerkleRoot(bytes32[] calldata roots) external onlyRole(ROOT_SETTER_ROLE) {
        for (uint256 i = 0; i < roots.length; i++) {
            if (roots[i] == bytes32(0)) revert MerkleRootNotSet();
            if (usedRoots[roots[i]]) continue; // Skip already used roots

            merkleRoots.push(roots[i]);
            usedRoots[roots[i]] = true;
        }

        emit BatchMerkleRootUpdated(roots);
    }

    /**
     * @dev Returns the number of Merkle roots
     * @return count Number of Merkle roots
     */
    function getMerkleRootCount() external view returns (uint256 count) {
        return merkleRoots.length;
    }

    /**
     * @dev Returns a specific Merkle root by index
     * @param index The index of the Merkle root
     * @return root The Merkle root at the given index
     */
    function getMerkleRootByIndex(uint256 index) external view returns (bytes32 root) {
        require(index < merkleRoots.length, "Index out of bounds");
        return merkleRoots[index];
    }

    /**
     * @dev Enhanced claim function with multiple root support
     * @param amount The amount of tokens to claim
     * @param proof The Merkle proof for verification
     * @param rootIndex The index of the Merkle root to use
     */
    function claimWithRootIndex(
        uint256 amount, 
        bytes32[] calldata proof, 
        uint256 rootIndex
    ) external whenNotPaused {
        if (claimed[msg.sender]) revert AlreadyClaimed();
        if (rootIndex >= merkleRoots.length) revert InvalidAmount();
        if (amount == 0) revert InvalidAmount();

        bytes32 currentRoot = merkleRoots[rootIndex];
        if (currentRoot == bytes32(0)) revert MerkleRootNotSet();

        // Create leaf hash using abi.encode for security
        bytes32 leaf = keccak256(abi.encode(msg.sender, amount));
        if (!MerkleProof.verify(proof, currentRoot, leaf)) revert InvalidProof();

        claimed[msg.sender] = true;
        totalClaimed += amount;
        totalClaimers += 1;

        // Mint token directly to the claimer
        token.mint(msg.sender, amount);
        emit Claimed(msg.sender, amount);
    }

    /**
     * @dev Batch claim function for multiple users
     * @param amounts Array of amounts to claim
     * @param proofs Array of Merkle proofs
     * @param rootIndex The index of the Merkle root to use
     */
    function batchClaim(
        uint256[] calldata amounts,
        bytes32[][] calldata proofs,
        uint256 rootIndex
    ) external whenNotPaused {
        require(amounts.length == proofs.length, "Arrays length mismatch");
        require(rootIndex < merkleRoots.length, "Invalid root index");

        bytes32 currentRoot = merkleRoots[rootIndex];
        if (currentRoot == bytes32(0)) revert MerkleRootNotSet();

        for (uint256 i = 0; i < amounts.length; i++) {
            if (amounts[i] == 0) continue;

            // Create leaf hash
            bytes32 leaf = keccak256(abi.encode(msg.sender, amounts[i]));
            if (!MerkleProof.verify(proofs[i], currentRoot, leaf)) continue;

            // Only claim if not already claimed
            if (!claimed[msg.sender]) {
                claimed[msg.sender] = true;
                totalClaimers += 1;
            }

            totalClaimed += amounts[i];
            token.mint(msg.sender, amounts[i]);
            emit Claimed(msg.sender, amounts[i]);
        }
    }

    /**
     * @dev Returns enhanced statistics
     * @return _totalClaimed Total amount claimed
     * @return _totalClaimers Total number of claimers
     * @return _merkleRootCount Number of Merkle roots
     * @return _version Contract version
     */
    function getEnhancedStats() external view returns (
        uint256 _totalClaimed,
        uint256 _totalClaimers,
        uint256 _merkleRootCount,
        string memory _version
    ) {
        return (totalClaimed, totalClaimers, merkleRoots.length, version);
    }

    // Reserve storage for future upgrades
    uint256[40] private __gap;
}

contracts/MaliciousAirDrop.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

/**
 * @title MaliciousAirDrop
 * @dev Malicious contract with different storage layout for testing
 * @author nexm
 */
contract MaliciousAirDrop is Initializable, AccessControlUpgradeable, UUPSUpgradeable {
    // This contract has a different storage layout than AirDrop
    // It will cause storage collision during upgrade

    bytes32 public constant ROOT_SETTER_ROLE = keccak256("ROOT_SETTER_ROLE");
    bytes32 public constant UPGRADER_ROLE = keccak256("UPGRADER_ROLE");

    // Different storage layout - this will cause issues
    uint256 public maliciousVariable1;
    uint256 public maliciousVariable2;
    bytes32 public merkleRoot;
    address public token;
    mapping(address => bool) public claimed;
    uint256 public upgradeAmount;
    uint256 public totalClaimed;
    uint256 public totalClaimers;

    // This is intentionally different from AirDrop's storage layout
    uint256 public maliciousVariable3;
    uint256 public maliciousVariable4;

    event MaliciousEvent(uint256 value);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address tokenAddress, bytes32 root) initializer public {
        __AccessControl_init();
        __UUPSUpgradeable_init();

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ROOT_SETTER_ROLE, msg.sender);
        _grantRole(UPGRADER_ROLE, msg.sender);

        token = tokenAddress;
        merkleRoot = root;
    }

    function setMaliciousVariable(uint256 value) external {
        maliciousVariable1 = value;
        emit MaliciousEvent(value);
    }

    function _authorizeUpgrade(address newImplementation)
        internal
        onlyRole(UPGRADER_ROLE)
        override
    {}

    // This will cause storage collision
    uint256[50] private __gap;
}

contracts/MyMintableToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

contract MyMintableToken is ERC20, AccessControl {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    constructor() ERC20("MyMintableToken", "MTK") {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
    }

    function mint(address to, uint256 amount) public onlyRole(MINTER_ROLE) {
        _mint(to, amount);
    }

    function burn(address from, uint256 amount) public onlyRole(MINTER_ROLE) {
        _burn(from, amount);
    }
}
